mapper.select:
select * from business_templates where name =#{name}

  and tenant=#{tenant}
 
  and status = 0 and is_public = #{isPublic} order by tag

select * from business_templates where status = 0 and is_public = false and name=#{name} and image_list like concat(concat('%',#{imageList}),'%')

  and tenant =#{tenant}

order by create_time
