mapper.select:
SELECT *
FROM configfile
WHERE project_id = #{projectId} AND name = #{name} AND reponame = #{repoName}
      AND cluster_id=#{clusterId} AND tags=#{tags}
ORDER BY create_time DESC
LIMIT 1

select * from configfile
where project_id=#{projectId}
and name=#{name}

    and reponame=#{repoName}


    and cluster_id=#{clusterId}

order by tags desc

select * from configfile


        project_id=#{projectId}


        and reponame=#{repoName}


        and ( name like concat('%',#{keyword},'%') or reponame like concat('%',#{keyword},'%'))


        and cluster_id in

            #{item}

select * from configfile


        project_id=#{projectId}


        and reponame=#{repoName}


        and cluster_id in

            #{item}



order by name

mapper.delete:
DELETE conItem, con FROM configfile_item conItem
right JOIN configfile con
ON conItem.configfile_id = con.id
where con.project_id=#{projectId} and con.name=#{name}

    and con.cluster_id=#{clusterId}

mapper.update:
update configfile
description = #{description}, reponame = #{repoName}, creator = #{user}, item = #{items}, path = #{path}
where id = #{id}

