mapper.select:
select count(*) from image_repository
where cluster_id = #{clusterId}
and project_id = #{projectId}

    and harbor_project_id = #{harborProjectId}


    and harbor_project_name = #{harborProjectName}

and is_default = 1 and is_normal = 1

